import React, { useRef, useEffect, useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { CssBaseline, Box, Container, Typography, Grid } from '@mui/material';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: { main: '#ffffff' },
    background: { default: '#000000', paper: '#121212' },
  },
  typography: { fontFamily: '"Helvetica Neue", Arial, sans-serif' },
});

const portfolioItems = [
  { title: 'Lighting', videoUrl: 'https://www.youtube.com/embed/moAfqFo3f00' },
  { title: 'Assistant Camera and Drone', videoUrl: 'https://www.youtube.com/embed/ULjGbYWbJDU' },
  { title: 'Camera', videoUrl: 'https://www.instagram.com/p/CzMeq4rPI6Z/embed' },
  { title: 'Drone', videoUrl: 'https://www.youtube.com/embed/HeZck91SBIY' },
  { title: 'General Production 1', videoUrl: 'https://www.youtube.com/embed/sc8DXDWRKac' },
  { title: 'General Production 2', videoUrl: 'https://www.youtube.com/embed/hklSInDi5Rc' },
];

export default function App() {
  const videoRef = useRef(null);
  const [videoLoaded, setVideoLoaded] = useState(false);

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.addEventListener('loadeddata', () => {
        setVideoLoaded(true);
        videoRef.current.play().catch(error => {
          console.error("Autoplay was prevented:", error);
        });
      });
    }
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <Box sx={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
          {!videoLoaded && (
            <Typography variant="h5" sx={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
              Loading video...
            </Typography>
          )}
          <video
            ref={videoRef}
            autoPlay
            loop
            muted
            playsInline
            style={{
              position: 'absolute',
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              opacity: videoLoaded ? 1 : 0,
              transition: 'opacity 0.5s ease-in-out',
            }}
          >
            <source src="https://drive.google.com/uc?export=download&id=1J8FMuuNqnrNKUdFAUxWhgA3BEngJtpjE" type="video/mov" />
            Your browser does not support the video tag.
          </video>
        </Box>
        <Container maxWidth="lg" sx={{ mt: 4, position: 'relative', zIndex: 1 }}>
          <Typography variant="h4" gutterBottom sx={{ textAlign: 'center', mb: 4 }}>
            Portfolio
          </Typography>
          <Grid container spacing={4}>
            {portfolioItems.map((item, index) => (
              <Grid item xs={12} md={6} key={index}>
                <Box sx={{ mb: 2 }}>
                  <Typography variant="h6" gutterBottom>
                    {item.title}
                  </Typography>
                  <Box sx={{ position: 'relative', paddingTop: '56.25%', overflow: 'hidden' }}>
                    <iframe
                      src={item.videoUrl}
                      title={item.title}
                      frameBorder="0"
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                      }}
                    />
                  </Box>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>
    </ThemeProvider>
  );
}
